from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Color, Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

from supportcode import next_program, prior_program

# Set up all devices.
HUB = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
Right_Move = Motor(Port.B, Direction.CLOCKWISE)
Left_Move = Motor(Port.E, Direction.COUNTERCLOCKWISE)
Movment_Motors = DriveBase(Left_Move, Right_Move, 88, 128)
Left_Gear = Motor(Port.A, Direction.CLOCKWISE)
Right_Gear = Motor(Port.C, Direction.CLOCKWISE)

# Initialize variables.
ACTIVEPROGRAM = 0
SPEED = 620
ACCELERATION = 500
TURN_SPEED = 300
TURN_ACCELERATION = 500
PROGRAM_LIST = ['1', '2', '3', '4', '5']
In_Leg = 0

async def Test_1():
    await wait(1)
    await Start_Task()
    await Movment_Motors.straight(500)
    await Movment_Motors.turn(45)
    await Movment_Motors.straight(-500)
    await Movment_Motors.turn(-45)

async def Start_Task():
    global In_Leg
    await wait(1)
    In_Leg = 1
    HUB.display.off()
    HUB.display.pixel(1, 1, 50)
    HUB.display.pixel(2, 2, 65)
    HUB.display.pixel(3, 3, 80)

async def main1():
    global ACTIVEPROGRAM
    HUB.imu.reset_heading(0)
    HUB.system.set_stop_button(None)
    Movment_Motors.settings(straight_speed=SPEED)
    Movment_Motors.settings(straight_acceleration=ACCELERATION)
    Movment_Motors.settings(turn_rate=TURN_SPEED)
    Movment_Motors.settings(turn_acceleration=TURN_ACCELERATION)
    Movment_Motors.use_gyro(True)
    while True:
        await wait(1)
        HUB.display.char(PROGRAM_LIST[ACTIVEPROGRAM])
        HUB.light.on(Color.MAGENTA)
        if Button.LEFT in HUB.buttons.pressed():
            ACTIVEPROGRAM = await prior_program(ACTIVEPROGRAM, PROGRAM_LIST)
            await HUB.speaker.beep(600, 199)
        elif Button.RIGHT in HUB.buttons.pressed():
            ACTIVEPROGRAM = await next_program(ACTIVEPROGRAM, PROGRAM_LIST)
            await HUB.speaker.beep(425, 199)
        elif Button.CENTER in HUB.buttons.pressed():
            if '1' in PROGRAM_LIST[ACTIVEPROGRAM]:
                await Test_1()
                await wait(250)
            elif '2' in PROGRAM_LIST[ACTIVEPROGRAM]:
                pass
            elif '3' in PROGRAM_LIST[ACTIVEPROGRAM]:
                pass
            elif '4' in PROGRAM_LIST[ACTIVEPROGRAM]:
                pass
            elif '5' in PROGRAM_LIST[ACTIVEPROGRAM]:
                pass
            else:
                pass
        else:
            await wait(10)

async def main2():
    while True:
        await wait(1)
        if In_Leg == 1:
            HUB.system.set_stop_button(Button.CENTER)
        else:
            await wait(155)
            HUB.system.set_stop_button(None)

async def main3():
    while True:
        await wait(1)
        await wait(3000)
        print('Gyro', HUB.imu.heading(), sep=", ")
        print('Name', 'Left', 'Right', sep=", ")
        print('Attachment Motors', Left_Gear.angle(), Right_Gear.angle(), sep=", ")
        print('Movement Motors', Left_Move.angle(), Right_Move.angle(), sep=", ")


async def main():
    await multitask(main1(), main2(), main3())

run_task(main())